const raw = $json?.body?.data || {};
const key = raw?.key || {};
const message = raw?.message || {};
const fromMe = key?.fromMe ?? false;

// Fun√ß√£o para gerar UUID v4
function generateUUID() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function (c) {
    const r = Math.random() * 16 | 0;
    const v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

// Captura e normaliza a mensagem de texto
let bodyMsg = message?.conversation || 
              message?.extendedTextMessage?.text || 
              message?.imageMessage?.caption ||
              message?.videoMessage?.caption || 
              '';
bodyMsg = bodyMsg?.toString().normalize().replace(/\s+/g, ' ').trim();

// Extrai n√∫mero do WhatsApp
const remoteJid = key?.remoteJid || '';
const number = remoteJid.split('@')[0]?.replace(/\D/g, '') || '';

// Garante sessionId v√°lido no formato UUID
let sessionId = key?.id;
if (!sessionId || typeof sessionId !== 'string' || !sessionId.match(/^[0-9a-f]{8}-[0-9a-f]{4}-4[0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i)) {
  sessionId = generateUUID();
}

// Detecta tipo de mensagem
let messageType = 'desconhecido';
const allTypes = Object.keys(message || {});

if (allTypes.includes('conversation')) {
  messageType = 'conversation';
} else if (allTypes.includes('extendedTextMessage')) {
  messageType = 'extendedTextMessage';
} else if (allTypes.includes('imageMessage')) {
  messageType = 'imageMessage';
} else if (allTypes.includes('audioMessage')) {
  messageType = 'audioMessage';
} else if (allTypes.includes('videoMessage')) {
  messageType = 'videoMessage';
}

// Regras de filtragem
let skip = false;
let skipReason = '';

if (fromMe) {
  skip = true;
  skipReason = 'Mensagem enviada pelo bot';
} else if (!bodyMsg) {
  skip = true;
  skipReason = 'Mensagem vazia';
} else if (!number) {
  skip = true;
  skipReason = 'N√∫mero inv√°lido';
} else if (messageType === 'desconhecido') {
  skip = true;
  skipReason = 'Tipo de mensagem desconhecido';
}

// Timestamp atual
const timestamp = new Date().toISOString();

// Cria uma hash simples da mensagem para comparar e evitar repeti√ß√£o
function createHash(text) {
  return Buffer.from(text).toString('base64').substring(0, 32);
}
const messageHash = bodyMsg ? createHash(bodyMsg) : null;

// Monta payload principal
const payload = {
  chatInput: bodyMsg,
  sessionId,
  number,
  message: bodyMsg,
  text: bodyMsg,
  messageType,
  skip,
  skipReason,
  timestamp,
  messageHash
};

// Prepara chave e valor para Redis
const redisKey = `mensagens:${sessionId}`;
const redisValue = JSON.stringify(payload);

return [{
  json: {
    ...payload,
    redisKey,
    redisValue
  }
}];

