// Captura a estrutura da mensagem recebida do Evolution API
const msg = $json?.messages?.[0] || {};
const fromMe = msg?.key?.fromMe ?? false;

const bodyMsg = $json?.mensagem || $json?.text || $json?.body || msg?.message?.conversation || '';

if (fromMe || !bodyMsg || bodyMsg.trim() === '') {
  return [{ json: { skip: true, reason: 'Mensagem vazia ou enviada pelo bot' } }];
}

const remoteJid = msg?.key?.remoteJid || '';
const number = remoteJid.split('@')[0]?.replace(/\D/g, '') || '';

if (!number) {
  return [{ json: { skip: true, reason: 'N√∫mero de telefone inv√°lido' } }];
}

// sessionId
const sessionId = $json?.sessionId || msg?.key?.id || $execution.id;

// Detecta tipo de mensagem
let messageType = 'desconhecido';
const allTypes = Object.keys(msg?.message || {});
if (allTypes.length > 0) {
  messageType = allTypes[0];
}

return [{
  json: {
    chatInput: bodyMsg.trim(),
    sessionId,
    number,
    message: bodyMsg.trim(),
    text: bodyMsg.trim(),
    messageType
  }
}];

// Obt√©m o primeiro item do fluxo
const item = $input.first();

// Garante que h√° dados na entrada
if (!item || !item.json || !item.json.output) {
  throw new Error("A chave 'output' n√£o foi encontrada no JSON de entrada.");
}

// Processa o texto removendo todas as quebras de linha e emojis
const textoEntrada = item.json.output; // Use a chave correta para pegar o texto de entrada

// Remove quebras de linha (\n)
let textoProcessado = textoEntrada.replace(/\n/g, "");

// Remove emojis
textoProcessado = textoProcessado.replace(
  /[\u{1F600}-\u{1F64F}]|[\u{1F300}-\u{1F5FF}]|[\u{1F680}-\u{1F6FF}]|[\u{1F700}-\u{1F77F}]|[\u{1F780}-\u{1F7FF}]|[\u{1F800}-\u{1F8FF}]|[\u{1F900}-\u{1F9FF}]|[\u{1FA00}-\u{1FA6F}]|[\u{1FA70}-\u{1FAFF}]|[\u{2600}-\u{26FF}]|[\u{2700}-\u{27BF}]|[\u{FE00}-\u{FE0F}]|[\u{1F1E0}-\u{1F1FF}]/gu,
  ""
);

// Retorna o resultado
return {
  textoOriginal: textoEntrada,
  textoSemQuebrasNemEmojis: textoProcessado,
};
