    } catch (err) {
      return {
        json: {
          error: "Erro ao converter bin√°rio para texto.",
          details: err.message,
          debug
        }
      };
    }
  }

  // Depois verifica se existe conte√∫do em json.data (Google Drive modo Text)
  else if (item.json?.data) {
    fileContent = item.json.data;
  } 

  // Se nenhum dos dois existir, retorna erro controlado
  else {
    return {
      json: {
        error: "Nenhum arquivo foi recebido ou o arquivo est√° vazio.",
        debug
      }
    };
  }

  // Processa o conte√∫do em linhas
