  const formattedMessage = `üåü *Mensagem do Dia* üåü\n\n${randomMessage}\n\n_Enviado automaticamente √†s 9h_ ‚è∞`;

  // Retorno final sempre em json (padr√£o n8n)
  return {
    json: {
      selected_message: randomMessage,
      formatted_message: formattedMessage,
