  const binaryData = $input.all()[0].binary.data.data;

  if (!binaryData) {
    throw new Error("Arquivo nÃ£o encontrado ou sem conteÃºdo.");
  }

  // âœ… Converte de base64 para texto
  const fileContent = Buffer.from(binaryData, 'base64').toString('utf-8');

  // âœ… Separa as mensagens (por 2 quebras de linha)
  const messages = fileContent.split('\n\n').filter(m => m.trim() !== '');

  if (messages.length === 0) {
    throw new Error('Nenhuma mensagem encontrada no arquivo');
  }

  // âœ… Seleciona a mensagem do dia
  const startDate = new Date("2025-06-17"); // Dia 01
  const today = new Date();
  const diffDays = Math.floor((today - startDate) / (1000 * 60 * 60 * 24));
  const diaIndex = diffDays % messages.length;

  const mensagemDoDia = messages[diaIndex];

  return [
    {
      json: {
        success: true,
        dia: diaIndex + 1,
        mensagem: mensagemDoDia,
        total_mensagens: messages.length,
      }
    }
  ];

} catch (error) {
