    categorias.anuncios.push(data);
  } else if (data.categoria === 'lancamento') {
    categorias.lancamentos.push(data);
  } else if (data.categoria === 'tecnologia') {
    categorias.tecnologias.push(data);
  } else if (data.categoria === 'insight') {
    categorias.insights.push(data);
  } else if (data.tipo === 'ponto_recarga') {
    categorias.pontos_recarga.push(data);
  } else if (data.tipo === 'supercharger') {
    categorias.superchargers.push(data);
  } else if (data.tipo === 'servico') {
    categorias.servicos.push(data);
  }
});

// Remove duplicatas dos anúncios
const uniqueAnuncios = new Map();
categorias.anuncios.forEach(anuncio => {
  const key = anuncio.titulo + anuncio.preco;
  if (!uniqueAnuncios.has(key)) {
    uniqueAnuncios.set(key, anuncio);
  }
});
categorias.anuncios = Array.from(uniqueAnuncios.values())
  .sort((a, b) => {
    const priceA = parseFloat((a.preco || '0').replace(/[^0-9,]/g, '').replace(',', '.'));
    const priceB = parseFloat((b.preco || '0').replace(/[^0-9,]/g, '').replace(',', '.'));
    return priceA - priceB;
  })
  .slice(0, 10);

return [{ json: categorias }];

