// 1. Normaliza√ß√£o, n√∫mero, sessionId e anti-repeti√ß√£o avan√ßado

const raw = $json.body?.data || $json.data || $json;
const key = raw?.key || {};
let message = raw?.message || $json.message || {};

// --- DETEC√á√ÉO ROBUSTA DE TIPO DE MENSAGEM ---
if (Object.keys(message).length === 0 && $json.body?.data?.message) {
  message = $json.body.data.message;
}
if (Object.keys(message).length === 0 && $json.data?.message) {
  message = $json.data.message;
}

let messageType = 'desconhecido';
const allTypes = Object.keys(message || {});
if (allTypes.includes('conversation')) {
  messageType = 'conversation';
} else if (allTypes.includes('extendedTextMessage')) {
  messageType = 'extendedTextMessage';
} else if (allTypes.includes('imageMessage')) {
  messageType = 'imageMessage';
} else if (allTypes.includes('audioMessage')) {
  messageType = 'audioMessage';
} else if (allTypes.includes('videoMessage')) {
  messageType = 'videoMessage';
}
if (messageType === 'desconhecido' && $json.messageType) {
  messageType = $json.messageType;
}

const fromMe = key?.fromMe ?? false;

// Gera UUID v4
function generateUUID() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function (c) {
    const r = Math.random() * 16 | 0, v = c === 'x' ? r : (r & 0x3 | 8);
    return v.toString(16);
  });
}

// CAPTURA mensagem em todos os caminhos poss√≠veis
let bodyMsg = '';
if (message?.conversation) {
  bodyMsg = message.conversation;
} else if (message?.extendedTextMessage?.text) {
  bodyMsg = message.extendedTextMessage.text;
} else if (message?.imageMessage?.caption) {
  bodyMsg = message.imageMessage.caption;
} else if (message?.videoMessage?.caption) {
  bodyMsg = message.videoMessage.caption;
} else if (message?.content) {
  bodyMsg = message.content;
} else if (raw?.message?.content) {
  bodyMsg = raw.message.content;
} else if (raw?.content) {
  bodyMsg = raw.content;
} else if ($json?.message?.content) {
  bodyMsg = $json.message.content;
} else if ($json?.content) {
  bodyMsg = $json.content;
} else if ($json.body?.data?.message?.conversation) {
  bodyMsg = $json.body.data.message.conversation;
} else if ($json.body?.data?.message?.content) {
  bodyMsg = $json.body.data.message.content;
} else if ($json.body?.data?.content) {
  bodyMsg = $json.body.data.content;
}

// Limpa mensagem (remove m√∫ltiplos espa√ßos, acentos e s√≠mbolos irrelevantes para hash)
bodyMsg = bodyMsg?.toString().normalize('NFD').replace(/[\u0300-\u036f]/g, '') // Remove acentos
                .replace(/[^\w\s@.:/-]/gi, '') // Remove s√≠mbolos (exceto e-mail, etc)
                .replace(/\s+/g, ' ').trim();

// Extrai chat_id e n√∫mero
const chat_id = key?.remoteJid || raw?.message?.chat_id || raw?.chat_id || '';
const number = chat_id.split('@')[0]?.replace(/\D/g, '') || '';

// Gera/garante sessionId UUID
let sessionId = key?.id || raw?.session_id || $json?.session_id;
if (!sessionId || typeof sessionId !== 'string' || !sessionId.match(/^[0-9a-f]{8}-[0-9a-f]{4}-4[0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i)) {
  sessionId = generateUUID();
}

// Normaliza timestamp (sempre ISO)
const timestamp = new Date().toISOString();

// --- C√ìDIGO ANTI-REPETI√á√ÉO/ANTI-FLOOD/BUFFER ---
// Hash da mensagem
function createHash(text) {
  return Buffer.from(text).toString('base64').substring(0, 32);
}
const messageHash = bodyMsg ? createHash(bodyMsg) : null;
const lastMsgHash = $json.lastMsgHash || null;

// Nova mensagem?
const isNewMsg = !lastMsgHash || (messageHash !== lastMsgHash);

// Controle de timeout/buffer (anti-flood)
const timeout_buffer = $json.timeout_buffer || 5;
const now = new Date();

let lastMsgTimestamp = $json.lastMsgTimestamp;
if (!lastMsgTimestamp && $json.messages) {
  try {
    const msgs = Array.isArray($json.messages) ? $json.messages : [];
    const validMsgs = msgs.filter(m => m && m.timestamp);
    validMsgs.sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp));
    lastMsgTimestamp = validMsgs[0]?.timestamp || null;
  } catch (e) { lastMsgTimestamp = null; }
}

let liberadoNoBuffer = true;
if (lastMsgTimestamp) {
  const ultimo = new Date(lastMsgTimestamp);
  liberadoNoBuffer = (now - ultimo) > (timeout_buffer * 1000);
}

// TRAVA: N√ÉO AVAN√áA se for do bot, mensagem vazia, n√∫mero inv√°lido, tipo desconhecido, mensagem repetida ou dentro do buffer!
let skip = false, skipReason = '', repeated = false;
if (fromMe) {
  skip = true; skipReason = 'Mensagem enviada pelo bot';
} else if (!bodyMsg) {
  skip = true; skipReason = 'Mensagem vazia';
} else if (!number) {
  skip = true; skipReason = 'N√∫mero inv√°lido';
} else if (messageType === 'desconhecido') {
  skip = true; skipReason = 'Tipo de mensagem desconhecido';
} else if (!isNewMsg) {
  skip = true; skipReason = 'Mensagem repetida do lead'; repeated = true;
} else if (!liberadoNoBuffer) {
  skip = true; skipReason = 'Mensagem dentro do timeout/buffer'; 
}

// Payload final
const payload = {
  chatInput: bodyMsg,
  sessionId,
  chat_id,
  number,
  message: bodyMsg,
  text: bodyMsg,
  messageType,
  skip,
  skipReason,
  timestamp,
  messageHash,
  lastMsgHash,
  repeated,
  isNewMsg,           // Use em IF para liberar s√≥ se true
  timeout_buffer,     // Para debug/controle
  lastMsgTimestamp,   // Timestamp da √∫ltima mensagem v√°lida (para buffer)
  liberadoNoBuffer    // TRUE = pode processar, FALSE = est√° dentro do timeout
};

const redisKey = `lastMsgHash:${chat_id}`;
const redisValue = messageHash;

// Log para troubleshooting
console.log('‚Üí [BUFFER] N√∫mero:', number);
console.log('‚Üí [BUFFER] √öltima mensagem timestamp:', lastMsgTimestamp);
console.log('‚Üí [BUFFER] Liberado?', liberadoNoBuffer);
console.log('‚Üí [HASH] messageHash:', messageHash, '| lastMsgHash:', lastMsgHash, '| isNewMsg:', isNewMsg);
console.log('‚Üí [SKIP] Motivo:', skipReason);

// RETORNA e j√° sinaliza skip (para travar no pr√≥ximo IF se precisar)
return [{
  json: {
    ...payload,
    redisKey,
    redisValue
  }
}];

let messageType = 'desconhecido';
const message = $json.message || {};

const allTypes = Object.keys(message);
if (allTypes.includes('conversation')) {
  messageType = 'conversation';
} else if (allTypes.includes('extendedTextMessage')) {
  messageType = 'extendedTextMessage';
} else if (allTypes.includes('audioMessage')) {
  messageType = 'audioMessage';
} else if (allTypes.includes('imageMessage')) {
  messageType = 'imageMessage';
}

// Padroniza e extrai campos para envio:
const numero = $json.phone || $json.number || $json.chat_id || message.chat_id || '';
const mensagem = 
    $json.output ||
    $json.chatInput ||
    $json.mensagem ||
    $json.text ||
    message.conversation ||
    message.extendedTextMessage?.text ||
    message.audioMessage?.caption ||
    message.imageMessage?.caption ||
    message.content ||
    '';

return [{
  json: {
    ...$json,
    messageType, // garante que o campo existe!
    numero,
    mensagem
  }
}];

// Espera que o node anterior trouxe do Redis:
// - $json.status_atendimento: valor salvo no Redis, tipo 'aguardando_resposta' ou 'liberado'

if ($json.status_atendimento === "aguardando_resposta") {
  // Se j√° est√° aguardando resposta, N√ÉO responde de novo
  return [{ json: { ...$json, podeResponder: false } }];
}

// Se n√£o est√° aguardando resposta, pode seguir
return [{ json: { ...$json, podeResponder: true } }];
