const leads = $input.all();
const agora = new Date();
const ultimasVintEQuatroHoras = new Date(agora.getTime() - (24 * 60 * 60 * 1000));
const leadsVerificados = [];

for (const lead of leads) {
  const leadData = lead.json;
  const ultimoLog = leadData.ultimo_followup_enviado;
  let podeEnviar = true;

  if (ultimoLog) {
    const dataUltimoLog = new Date(ultimoLog);
    if (dataUltimoLog > ultimasVintEQuatroHoras) {
      podeEnviar = false;
      leadData.motivo_reprovado = 'J√° enviado nas √∫ltimas 24h';
    }
  }

  // Verificar hor√°rio comercial (9h √†s 18h)
  const horaAtual = agora.getHours();
  const diaAtual = agora.getDay(); // 0 = domingo, 6 = s√°bado
  const isHorarioComercial = (diaAtual >= 1 && diaAtual <= 6) && (horaAtual >= 9 && horaAtual <= 18);

  if (!isHorarioComercial) {
    leadData.motivo_reprovado = 'Fora do hor√°rio comercial';
  }

  if (podeEnviar && isHorarioComercial) {
    leadsVerificados.push({
      json: {
        ...leadData,
        verificado_duplicata: true,
        horario_comercial: true
      }
    });
  }
}

if (leadsVerificados.length === 0) {
  // Para debug: retorne todos os leads, mas marcados com o motivo da reprova√ß√£o
  return leads.map(lead => ({
    json: {
      ...lead.json,
      aprovado: false,
      motivo_reprovado: lead.json.motivo_reprovado || 'Reprovado por crit√©rio'
    }
  }));
}

return leadsVerificados;

// Processar estrat√©gia inteligente de follow-up com personaliza√ß√£o avan√ßada
const leads = $input.all();
const processedLeads = [];

for (const lead of leads) {
  const leadData = lead.json;

  // Calcular m√©tricas do lead
  const ultimoContato = new Date(leadData.ultimo_contato || leadData.created_at);
  const diasSemContato = Math.floor((Date.now() - ultimoContato.getTime()) / (1000 * 60 * 60 * 24));
  const tentativas = leadData.tentativas_followup || 0;
  const nomeCompleto = leadData.name || leadData.nome_completo || '';
  const nomeCurto = nomeCompleto.split(' ')[0] || 'Amigo(a)';
  const segmento = leadData.segmento || 'geral';
  const origem = leadData.origem || 'site';

  // Personaliza√ß√£o baseada no perfil do lead
  let tipoFollowup = '';
  let mensagem = '';
  let prioridadeMsg = 'normal';

  // Estrat√©gias diferenciadas por tentativa e perfil
  if (tentativas === 0) {
    tipoFollowup = 'boas_vindas';
    prioridadeMsg = 'alta';

    if (segmento === 'e-commerce') {
      mensagem = `Oi ${nomeCurto}! üõí Vi que voc√™ tem interesse em automa√ß√£o para e-commerce. Nossos clientes aumentam as vendas em at√© 40% com nossas solu√ß√µes. Quer saber como?`;
    } else if (segmento === 'servicos') {
      mensagem = `${nomeCurto}, tudo bem? üíº Automa√ß√£o para prestadores de servi√ßos √© nossa especialidade! Que tal economizar 20h/semana em tarefas repetitivas?`;
    } else {
      mensagem = `Oi ${nomeCurto}! üòä Obrigado pelo interesse em automa√ß√£o. Vamos conversar sobre como podemos otimizar seus processos e aumentar sua produtividade?`;
    }

  } else if (tentativas === 1) {
    tipoFollowup = 'valor_agregado';
    prioridadeMsg = 'alta';

    const casosDeSuccesso = {
      'e-commerce': 'Recentemente ajudamos uma loja online a automatizar todo o processo de p√≥s-venda e eles aumentaram a reten√ß√£o de clientes em 35%',
      'servicos': 'Um cliente nosso de consultoria conseguiu dobrar a capacidade de atendimento sem contratar mais funcion√°rios',
      'geral': 'Nossos clientes economizam em m√©dia R$ 8.500/m√™s s√≥ em custos operacionais'
    };

    mensagem = `${nomeCurto}, imagino que deve estar corrido! üöÄ ${casosDeSuccesso[segmento] || casosDeSuccesso.geral}. Que tal 15 minutos para mostrar como isso pode funcionar no seu neg√≥cio?`;

  } else if (tentativas === 2) {
    tipoFollowup = 'urgencia_social';
    prioridadeMsg = 'media';

    mensagem = `${nomeCurto}, √∫ltima semana tivemos 12 novos clientes! üìà O mercado est√° acelerando e quem n√£o automatizar vai ficar para tr√°s. Vamos conversar antes que sua vaga seja preenchida?`;

  } else if (tentativas === 3) {
    tipoFollowup = 'oferta_especial';
    prioridadeMsg = 'media';

    mensagem = `${nomeCurto}, estou com uma proposta especial! üéØ Para leads antigos como voc√™, estou oferecendo 30% de desconto na primeira automa√ß√£o. Mas s√≥ at√© sexta. Interessado?`;

  } else if (tentativas === 4) {
    tipoFollowup = 'despedida_inteligente';
    prioridadeMsg = 'baixa';

    mensagem = `${nomeCurto}, sei que deve estar ocupado(a) demais para automa√ß√£o agora. üòÖ Vou parar de insistir, mas se mudar de ideia, √© s√≥ chamar. Sucesso a√≠! üöÄ`;
  }

  // Formata√ß√£o inteligente do telefone
  let telefoneFormatado = '';
  if (leadData.phone) {
    const phoneClean = leadData.phone.toString().replace(/\D/g, '');
    telefoneFormatado = phoneClean.length === 11 ? `55${phoneClean}` : `55${phoneClean.slice(-11)}`;
  } else if (leadData.telefone_formatado) {
    telefoneFormatado = leadData.telefone_formatado;
  }

  // Score de convers√£o baseado em m√∫ltiplos fatores
  let scoreConversao = 50; // Base
  if (diasSemContato <= 3) scoreConversao += 20;
  if (origem === 'indicacao') scoreConversao += 30;
  if (leadData.interacoes_site > 5) scoreConversao += 15;
  if (segmento === 'e-commerce') scoreConversao += 10;

  processedLeads.push({
    json: {
      ...leadData,
      telefone_formatado: telefoneFormatado,
      nome_curto: nomeCurto,
      tipo_followup: tipoFollowup,
      mensagem_followup: mensagem,
      dias_sem_contato: diasSemContato,
      nova_tentativa: tentativas + 1,
      prioridade_mensagem: prioridadeMsg,
      score_conversao: scoreConversao,
      timestamp_processamento: new Date().toISOString()
    }
  });
}

// Ordenar por prioridade e score
processedLeads.sort((a, b) => {
  const prioridadeOrder = { 'alta': 3, 'media': 2, 'baixa': 1 };
  const prioridadeA = prioridadeOrder[a.json.prioridade_mensagem];
  const prioridadeB = prioridadeOrder[b.json.prioridade_mensagem];

  if (prioridadeA !== prioridadeB) return prioridadeB - prioridadeA;
  return b.json.score_conversao - a.json.score_conversao;
});

return processedLeads;

