// Acessa todos os itens que chegam ao nó
const items = $input.all();

// Lista de cidades/bairros conhecidas (você pode expandir)
const cidadesEBairros = [
  'são paulo', 'rio de janeiro', 'curitiba', 'belo horizonte',
  'porto alegre', 'salvador', 'recife', 'florianópolis',
  'campinas', 'guarulhos', 'osasco', 'santos', 'diadema',
  'vila mariana', 'tatuapé', 'moema', 'ipiranga', 'botafogo'
];

// Função auxiliar para detectar tipo de dado
function detectarCampo(valor) {
  if (!valor) return null;
  const texto = valor.trim().toLowerCase();

  // Detecta telefone (apenas números e +)
  if (/^\+?\d{10,15}$/.test(texto.replace(/\D/g, ''))) {
    return 'telefone';
  }

  // Detecta e-mail
  if (/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(texto)) {
    return 'email';
  }

  // Detecta tipo_cliente
  if (['b2b', 'b2c'].includes(texto)) {
    return 'tipo_cliente';
  }

  // Detecta cidade/bairro
  if (cidadesEBairros.some(c => texto.includes(c))) {
    return 'cidade';
  }

  // Se nenhuma das regras acima, assume que é nome
  return 'nome';
}

// Itera sobre cada item
for (const item of items) {
  const mensagem = item.json.mensagem;
  if (!mensagem) continue;

  // Divide a mensagem por vírgulas
  const partes = mensagem.split(',').map(p => p.trim()).filter(Boolean);

  // Inicializa campos
  let nome = null;
  let telefone = null;
  let email = null;
  let tipo_cliente = null;
  let cidade = null;

  // Classifica cada parte conforme o tipo detectado
  for (const parte of partes) {
    const tipo = detectarCampo(parte);
    switch (tipo) {
      case 'nome':
        nome = parte;
        break;
      case 'telefone':
        telefone = parte;
        break;
      case 'email':
        email = parte;
        break;
      case 'tipo_cliente':
        tipo_cliente = parte.toLowerCase();
        break;
      case 'cidade':
        cidade = parte;
        break;
    }
  }

  // Adiciona os campos detectados ao JSON de saída
  Object.assign(item.json, { nome, telefone, email, tipo_cliente, cidade });
}

// Retorna os itens modificados
return items;
