// n8n Code node (JavaScript)
// Extrai métricas de LLM por nó a partir do Execution Data, sem usar this/expressions dentro do Code.

// --------------------- Helpers ---------------------
function safeNum(v, d = 0) {
  const n = Number(v);
  return Number.isFinite(n) ? n : d;
}

function detectProvider(nodeName = "", json = {}) {
  const name = (nodeName || "").toLowerCase();
  const p = (json.provider || "").toLowerCase();

  if (p.includes("openai") || name.includes("openai")) return "openai";
  if (p.includes("anthropic") || name.includes("anthropic") || name.includes("claude")) return "anthropic";
  if (p.includes("google") || name.includes("gemini") || name.includes("google")) return "google";
  if (p.includes("mistral") || name.includes("mistral")) return "mistral";
  if (p.includes("cohere") || name.includes("cohere")) return "cohere";
  return "openai"; // default
}

function detectModel(nodeExecution = {}, outputJson = {}) {
  return (
    outputJson.model ||
    outputJson.response?.model ||
    outputJson.data?.model ||
    nodeExecution?.source?.main?.[0]?.[0]?.json?.model ||
    "gpt-4o-mini"
  );
}

function extractUsage(outputJson = {}) {
  const u = outputJson.usage || outputJson.token_usage || outputJson.metrics?.usage || {};
  const prompt =
    safeNum(u.prompt_tokens) ||
    safeNum(u.input_tokens) ||
    safeNum(outputJson.input_tokens) ||
    0;

  const completion =
    safeNum(u.completion_tokens) ||
    safeNum(u.output_tokens) ||
    safeNum(outputJson.output_tokens) ||
    0;

  const total =
    safeNum(u.total_tokens) ||
    (prompt + completion);

  return { prompt, completion, total };
}

function firstDefined(...vals) {
  for (const v of vals) if (v !== undefined && v !== null && v !== "") return v;
  return undefined;
}

// --------------------- Execução ---------------------
const items = $input.all();
const out = [];
const nowIso = new Date().toISOString();

for (const item of items) {
  const execution = item.json || {};

  // Inferir workflow/run de múltiplas fontes + permitir injeção prévia via Set
  const workflowId = firstDefined(
    execution.workflowId,
    execution.workflow_id,
    execution.workflow?.id,
    execution.data?.workflowData?.id,
    "unknown_wf"
  );

  const workflowName = firstDefined(
    execution.workflowName,
    execution.workflow_name,
    execution.workflow?.name,
    execution.data?.workflowData?.name,
    "unknown_wf_name"
  );

  const runId = firstDefined(
    execution.run_id,
    execution.id,
    execution.executionId,
    execution.data?.executionId,
    execution.data?.metadata?.executionId,
    "unknown_run"
  );

  const startedAt = firstDefined(
    execution.startedAt,
    execution.ts,
    execution.started_at,
    execution.data?.startedAt,
    nowIso
  );

  const status = (typeof execution.finished === "boolean")
    ? (execution.finished ? "success" : "running")
    : firstDefined(execution.status, "success");

  const base = {
    workflow_id: workflowId,
    workflow_name: workflowName,
    run_id: runId,
    ts: startedAt,
    status,
    user_id: firstDefined(execution.userId, execution.user_id, "system"),
  };

  // Caminho canônico do n8n Execution Data
  const runData = execution?.data?.resultData?.runData;

  if (!runData || typeof runData !== "object") {
    // Fallback: tentar extrair direto do item atual (útil quando encadeado logo após um nó LLM)
    const usage = extractUsage(execution);
    if (usage.total > 0) {
      const provider = detectProvider(execution.node_name || "", execution);
      const model = detectModel({}, execution);
      out.push({
        json: {
          ...base,
          node_name: execution.node_name || "LLM Node",
          provider,
          model,
          input_tokens: usage.prompt,
          output_tokens: usage.completion,
          total_tokens: usage.total,
          latency_ms: safeNum(execution.latency_ms, 0),
        },
      });
    } else {
      out.push({ json: { ...base, no_llm_usage: true } });
    }
    continue;
  }

  // Varre nós
  for (const nodeName of Object.keys(runData)) {
    const nodeExecs = runData[nodeName];
    if (!Array.isArray(nodeExecs)) continue;

    for (const nodeExecution of nodeExecs) {
      const mains = nodeExecution?.data?.main;
      if (!Array.isArray(mains) || mains.length === 0) continue;

      for (const main of mains) {
        if (!Array.isArray(main) || main.length === 0) continue;

        for (const outputItem of main) {
          const j = outputItem?.json || {};
          const usage = extractUsage(j);
          if (usage.total > 0) {
            const provider = detectProvider(nodeName, j);
            const model = detectModel(nodeExecution, j);

            out.push({
              json: {
                ...base,
                node_name: nodeName,
                provider,
                model,
                input_tokens: usage.prompt,
                output_tokens: usage.completion,
                total_tokens: usage.total,
                latency_ms: safeNum(nodeExecution?.executionTime, 0),
              },
            });
          }
        }
      }
    }
  }
}

return out.length > 0 ? out : [{ json: { no_llm_usage: true } }];

// Processar dados recebidos via webhook
const item = $input.first();
const data = item.json.body || item.json;

// Estrutura esperada do webhook:
// {
//   workflow_id: string,
//   workflow_name: string,
//   run_id: string,
//   node_name: string,
//   provider: string,
//   model: string,
//   usage: {
//     prompt_tokens: number,
//     completion_tokens: number,
//     total_tokens: number
//   },
//   latency_ms: number
// }

const processedData = {
  ts: new Date().toISOString(),
  workflow_id: data.workflow_id || $workflow.id,
  workflow_name: data.workflow_name || $workflow.name,
  run_id: data.run_id || $runId,
  node_name: data.node_name || 'Unknown',
  provider: data.provider || 'openai',
  model: data.model || 'gpt-4o-mini',
  input_tokens: data.usage?.prompt_tokens || data.usage?.input_tokens || 0,
  output_tokens: data.usage?.completion_tokens || data.usage?.output_tokens || 0,
  total_tokens: data.usage?.total_tokens || ((data.usage?.prompt_tokens || 0) + (data.usage?.completion_tokens || 0)),
  latency_ms: data.latency_ms || 0,
  status: data.status || 'success',
  user_id: data.user_id || 'webhook'
};

return [{ json: processedData }];
