const output = $input.item.json.stdout || '';
const lines = output.split('\n');

const metrics = {};
lines.forEach(line => {
  const parts = line.split(': ');
  if (parts.length === 2) {
    metrics[parts[0]] = parts[1].trim();
  }
});

// Valores padr√£o caso n√£o existam
metrics.LOAD_AVERAGE = metrics.LOAD_AVERAGE || 'N/A';
metrics.CPU_USAGE_PERCENT = metrics.CPU_USAGE_PERCENT || '0';
metrics.MEMORY_USAGE_PERCENT = metrics.MEMORY_USAGE_PERCENT || '0';
metrics.DISK_USAGE_PERCENT = metrics.DISK_USAGE_PERCENT || '0';
metrics.RUNNING_CONTAINERS = metrics.RUNNING_CONTAINERS || '0';
metrics.DISK_STATUS = metrics.DISK_STATUS || 'OK';
metrics.MEMORY_STATUS = metrics.MEMORY_STATUS || 'OK';

let insights = `<h2>üöÄ Relat√≥rio de Sa√∫de da VPS</h2>`;
insights += `<ul>`;
insights += `<li><strong>Carga do Sistema:</strong> ${metrics.LOAD_AVERAGE}</li>`;
insights += `<li><strong>Uso de CPU:</strong> ${metrics.CPU_USAGE_PERCENT}%</li>`;
insights += `<li><strong>Uso de Mem√≥ria:</strong> ${metrics.MEMORY_USAGE_PERCENT}%</li>`;
insights += `<li><strong>Uso de Disco:</strong> ${metrics.DISK_USAGE_PERCENT}%</li>`;
insights += `<li><strong>Cont√™ineres Ativos:</strong> ${metrics.RUNNING_CONTAINERS}</li>`;
insights += `</ul>`;

let recommendations = `<h3>üí° Recomenda√ß√µes</h3>`;
recommendations += `<ul>`;
let hasRecommendations = false;
let isCritical = false;

if (metrics.DISK_STATUS === 'CRITICAL') {
  recommendations += `<li><strong style="color: #ff8c00;">‚ö†Ô∏è ATEN√á√ÉO:</strong> O uso de disco est√° em <strong>${metrics.DISK_USAGE_PERCENT}%</strong>. Considere executar uma limpeza de logs ou imagens Docker n√£o utilizadas.</li>`;
  hasRecommendations = true;
  isCritical = true;
}

if (metrics.MEMORY_STATUS === 'CRITICAL') {
  recommendations += `<li><strong style="color: #dc143c;">üö® CR√çTICO:</strong> O uso de mem√≥ria est√° em <strong>${metrics.MEMORY_USAGE_PERCENT}%</strong>. Verifique os cont√™ineres que mais consomem mem√≥ria com o comando <code>docker stats</code>.</li>`;
  hasRecommendations = true;
  isCritical = true;
}

if (!hasRecommendations) {
  recommendations += `<li><strong style="color: #32cd32;">‚úÖ TUDO OK:</strong> Nenhum problema detectado. O sistema est√° operando normalmente.</li>`;
}
recommendations += `</ul>`;

const timestamp = new Date().toLocaleString('pt-BR', { timeZone: 'America/Sao_Paulo' });

const finalHtmlMessage = `
<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
</head>
<body style="font-family: Arial, sans-serif; line-height: 1.6; color: #333; background-color: #f4f4f4; margin: 0; padding: 20px;">
  <div style="max-width: 650px; margin: auto; padding: 20px; background: white; border: 1px solid #ddd; border-radius: 10px; box-shadow: 0 2px 8px rgba(0,0,0,0.1);">
    ${insights}
    <hr style="border: 0; border-top: 1px solid #eee; margin: 20px 0;">
    ${recommendations}
    <hr style="border: 0; border-top: 1px solid #eee; margin: 20px 0;">
    <p style="font-size: 12px; color: #999; text-align: center;">Relat√≥rio gerado em: ${timestamp}</p>
  </div>
</body>
</html>
`;

return {
  htmlMessage: finalHtmlMessage,
  subject: `Relat√≥rio de Sa√∫de VPS: ${isCritical ? '‚ö†Ô∏è A√á√ÉO NECESS√ÅRIA' : '‚úÖ Tudo OK'}`,
  metrics: metrics,
  isCritical: isCritical
};

// Log do erro para debug
const errorData = $input.item.json;
console.log('Erro capturado:', errorData);

const errorMessage = errorData.message || 'Erro desconhecido';
const errorNode = errorData.node || 'Desconhecido';

const htmlError = `
<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8">
</head>
<body style="font-family: Arial, sans-serif; line-height: 1.6; color: #333;">
  <div style="max-width: 650px; margin: auto; padding: 20px; border: 2px solid #dc143c; border-radius: 10px; background-color: #fff5f5;">
    <h2 style="color: #dc143c;">üö® Erro no Monitoramento da VPS</h2>
    <p><strong>N√≥ com erro:</strong> ${errorNode}</p>
    <p><strong>Mensagem:</strong> ${errorMessage}</p>
    <hr style="border: 0; border-top: 1px solid #eee; margin: 20px 0;">
    <p style="font-size: 14px; color: #666;">Verifique a conex√£o SSH e os scripts no servidor.</p>
    <p style="font-size: 12px; color: #999;">Data: ${new Date().toLocaleString('pt-BR', { timeZone: 'America/Sao_Paulo' })}</p>
  </div>
</body>
</html>
`;

return {
  subject: 'üö® ERRO - Monitoramento VPS Falhou',
  htmlMessage: htmlError
};
