const output = $json.stdout;
const lines = output.split('\n');

const metrics = {};
lines.forEach(line => {
  const parts = line.split(': ');
  if (parts.length === 2) {
    metrics[parts[0]] = parts[1].trim();
  }
});

let insights = `## üöÄ Relat√≥rio de Sa√∫de da VPS\n\n`;
let recommendations = `### üí° Recomenda√ß√µes\n\n`;
let hasRecommendations = false;

// Adiciona m√©tricas ao relat√≥rio
insights += `*   **Carga do Sistema:** ${metrics.LOAD_AVERAGE}\n`;
insights += `*   **Uso de CPU:** ${metrics.CPU_USAGE_PERCENT}%\n`;
insights += `*   **Uso de Mem√≥ria:** ${metrics.MEMORY_USAGE_PERCENT}%\n`;
insights += `*   **Uso de Disco:** ${metrics.DISK_USAGE_PERCENT}%\n`;
insights += `*   **Cont√™ineres Ativos:** ${metrics.RUNNING_CONTAINERS}\n\n`;

// Gera recomenda√ß√µes com base nos status
if (metrics.DISK_STATUS === 'CRITICAL') {
  recommendations += `*   ‚ö†Ô∏è **Aten√ß√£o:** O uso de disco est√° em **${metrics.DISK_USAGE_PERCENT}%**. Considere executar uma limpeza de logs ou imagens Docker n√£o utilizadas.\n`;
  hasRecommendations = true;
}

if (metrics.MEMORY_STATUS === 'CRITICAL') {
  recommendations += `*   üö® **Cr√≠tico:** O uso de mem√≥ria est√° em **${metrics.MEMORY_USAGE_PERCENT}%**. Verifique os cont√™ineres que mais consomem mem√≥ria com o comando \`docker stats\`.\n`;
  hasRecommendations = true;
}

if (!hasRecommendations) {
  recommendations += `*   ‚úÖ Tudo parece estar operando normalmente.\n`;
}

// Monta a mensagem final
const finalMessage = insights + recommendations;

// Retorna o objeto formatado
return { 
  message: finalMessage,
  metrics: metrics
};
