// Remove duplicatas e organiza dados
const items = $input.all();
const uniqueItems = new Map();

items.forEach(item => {
  const key = item.json.titulo + item.json.preco;
  if (!uniqueItems.has(key) && !item.json.erro) {
    uniqueItems.set(key, item.json);
  }
});

const sortedItems = Array.from(uniqueItems.values())
  .sort((a, b) => {
    const priceA = parseFloat(a.preco.replace(/[^0-9,]/g, '').replace(',', '.'));
    const priceB = parseFloat(b.preco.replace(/[^0-9,]/g, '').replace(',', '.'));
    return priceA - priceB;
  });

return sortedItems.map(item => ({ json: item }));

