parameters.jsCode:
// Acessa os itens que chegam no nó
const items = $input.all();

// Itera sobre cada item de entrada (geralmente apenas um)
for (const item of items) {
  // Pega o valor do campo 'mensagem' do item atual
  const mensagem = item.json.mensagem;

  // Divide a string. O .trim() em cada parte remove espaços em branco extras
  const partes = mensagem.split(',');
  const nome = partes[0] ? partes[0].trim() : null;
  const telefone = partes[1] ? partes[1].trim() : null;
  const email = partes[2] ? partes[2].trim() : null;
  const tipo_cliente = partes[3] ? partes[3].trim() : null;
  const cidade = partes[4] ? partes[4].trim() : null;

  // Adiciona os novos campos ao item
  item.json.nome = nome;
  item.json.telefone = telefone;
  item.json.email = email;
  item.json.tipo_cliente = tipo_cliente;
  item.json.cidade = cidade
}

// Retorna os itens modificados para serem usados nos próximos nós
return items;
