// IMPORTANTE: Referencia o nó do WEBHOOK, não o resultado do Supabase
const webhookData = $('Webhook').first().json;

// Pega o telefone do WhatsApp (identificador único)
let telefone_whatsapp = webhookData.mobile || webhookData.body?.data?.key?.remoteJid?.split('@')[0] || null;

// Remove :80 ou qualquer sufixo similar do telefone
if (telefone_whatsapp) {
  telefone_whatsapp = telefone_whatsapp.split(':')[0]; // Remove tudo depois de ":"
  telefone_whatsapp = telefone_whatsapp.replace(/\D/g, ''); // Remove caracteres não numéricos
}

// Pega o nome do contato do WhatsApp  
const nome_whatsapp = webhookData.body?.data?.pushName || null;

// Pega a mensagem
const mensagem = webhookData.mensagem || webhookData.body?.data?.message?.conversation || "";

// Extrai dados da mensagem (caso o lead envie dados estruturados)
const partes = mensagem.split(',').map(p => p.trim());

// Extrai informações
const nome_extraido = partes.find(p => /^[a-zA-ZÀ-ÿ\s]+$/.test(p) && p.length > 3) || null;
let telefone_extraido = partes.find(p => /^\+?\d{10,15}$/.test(p.replace(/\D/g, ''))) || null;

// Limpa telefone extraído também
if (telefone_extraido) {
  telefone_extraido = telefone_extraido.replace(/\D/g, ''); // Remove tudo que não é número
}

const email = partes.find(p => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(p)) || null;
const tipo_cliente = partes.find(p => ['b2b','b2c'].includes(p.toLowerCase()))?.toLowerCase() || null;

// Cidades
const cidades = ['são paulo','rio de janeiro','curitiba','salvador','belo horizonte','campinas','guarulhos','osasco','santos','brasília','fortaleza','manaus','recife','porto alegre'];
const cidade = partes.find(p => cidades.some(c => p.toLowerCase().includes(c))) || null;

// Define valores finais
const telefone_principal = telefone_whatsapp || telefone_extraido;
const nome_final = nome_extraido || nome_whatsapp || 'Lead sem nome';

// Verifica se o lead já existe (vem do nó "Get a row")
const leadExistente = $input.first().json;
const jaExiste = leadExistente && leadExistente.id;

// Monta objeto para Supabase
const dadosParaSupabase = {
  telefone: telefone_principal,
  nome: nome_final,
  email: email || (jaExiste ? leadExistente.email : null),
  tipo_cliente: tipo_cliente || (jaExiste ? leadExistente.tipo_cliente : null),
  cidade: cidade || (jaExiste ? leadExistente.cidade : null),
  mensagem_original: jaExiste ? leadExistente.mensagem_original : mensagem,
  ultima_mensagem: mensagem,
  instance: webhookData.body?.instance || webhookData.Instancia_Evolution || 'grd-test',
  status: jaExiste ? leadExistente.status : 'novo',
  updated_at: new Date().toISOString()
};

// Se for lead novo, adiciona created_at
if (!jaExiste) {
  dadosParaSupabase.created_at = new Date().toISOString();
}

return [{
  json: dadosParaSupabase
}];

