// Formatar mensagens WhatsApp individuais para clientes
const tenantData = $('Processar Tenants').item.json;
const processedItems = [];

const hoje = new Date().toLocaleDateString('pt-BR');

for (const parcela of tenantData.parcelas) {
  // SÃ³ processar se tiver telefone
  if (!parcela.cliente_phone) {
    continue;
  }

  const valorFormatado = parcela.valor_mensagem.toFixed(2).replace('.', ',');

  let mensagem = `ðŸ”” *Lembrete de Vencimento*\n\n`;
  mensagem += `OlÃ¡ *${parcela.cliente_nome}*!\n\n`;
  mensagem += `Sua ${parcela.tipo_valor.toLowerCase()} #${parcela.numero_parcela} no valor de *R$ ${valorFormatado}* vence hoje (${hoje}).\n\n`;
  mensagem += `Por favor, realize o pagamento para evitar juros e multa.\n\n`;
  mensagem += `*Telefone para contato:* ${parcela.cliente_phone.replace(/^55/, '')}\n\n`;
  mensagem += `Obrigado!`;

  processedItems.push({
    json: {
