// Processar parcelas e agrupar por tenant
const items = $input.all();
const tenantsMap = {};

for (const item of items) {
  const parcela = item.json;
  const tenantId = parcela.tenant_id;

  if (!tenantsMap[tenantId]) {
    tenantsMap[tenantId] = {
      tenant_id: tenantId,
      tenant_name: parcela.tenant_name,
      parcelas: [],
      total_valor: 0,
      total_parcelas: 0
    };
  }

  // Calcular valor em aberto
  const valorAberto = parseFloat(parcela.valor_parcela || 0) - parseFloat(parcela.valor_pago || 0);

  // Determinar telefone do cliente (prioridade: whatsapp > celular > telefone)
  let telefone = parcela.whatsapp || parcela.celular || parcela.telefone;
  telefone = telefone ? telefone.replace(/\D/g, '') : null;
  if (telefone && !telefone.startsWith('55')) {
    telefone = '55' + telefone;
  }

  // Determinar tipo e valor para mensagem
  const modelo = parcela.modelo_emprestimo || 'PRICE';
  let tipoValor = 'Parcela';
  let valorMensagem = valorAberto;

  if (modelo === 'INTEREST_ONLY') {
    tipoValor = 'Juros';
    valorMensagem = parseFloat(parcela.interest_amount || 0) || valorAberto;
  }

  tenantsMap[tenantId].parcelas.push({
    parcela_id: parcela.parcela_id,
    cliente_id: parcela.client_id,
    cliente_nome: parcela.cliente_nome,
    cliente_phone: telefone,
    cliente_email: parcela.cliente_email,
    numero_parcela: parcela.numero_parcela,
    valor_aberto: valorAberto,
    tipo_valor: tipoValor,
    valor_mensagem: valorMensagem,
    status: parcela.status,
    data_vencimento: parcela.data_vencimento,
    modelo_emprestimo: modelo
  });

  tenantsMap[tenantId].total_valor += valorAberto;
  tenantsMap[tenantId].total_parcelas += 1;
}

// Converter para array
const processedItems = Object.values(tenantsMap).map(tenant => ({
  json: tenant
}));

return processedItems;

