// ============================================
// MIDDLEWARE DE AUTENTICAÇÃO JWT - VERSÃO SEGURA
// Valida assinatura, expiração e campos obrigatórios
// ============================================

const crypto = require('crypto');

// IMPORTANTE: Configure sua chave secreta nas variáveis de ambiente
const JWT_SECRET = process.env.JWT_SECRET || 'YOUR_SECRET_KEY_CHANGE_ME';

if (JWT_SECRET === 'YOUR_SECRET_KEY_CHANGE_ME') {
  console.warn('⚠️  ATENÇÃO: Configure JWT_SECRET nas variáveis de ambiente!');
}

function getAuthHeader(headers) {
  if (!headers) return '';
  return (
    headers.authorization ||
    headers.Authorization ||
    headers.AUTHORIZATION ||
    headers['x-authorization'] ||
    headers['X-Authorization'] ||
    ''
  );
}

function base64UrlDecode(str) {
  const pad = str.length % 4 === 0 ? '' : '='.repeat(4 - (str.length % 4));
  const b64 = (str + pad).replace(/-/g, '+').replace(/_/g, '/');
  return Buffer.from(b64, 'base64').toString('utf8');
}

function verifyJwtSignature(header, payload, signature, secret) {
  const data = `${header}.${payload}`;
  const expectedSignature = crypto
    .createHmac('sha256', secret)
    .update(data)
    .digest('base64url');

  return signature === expectedSignature;
}

const headers = $json.headers || {};
const authHeader = getAuthHeader(headers);

// Validação 1: Header Authorization presente
if (!authHeader || !authHeader.startsWith('Bearer ')) {
  return [{ 
    json: { 
      autenticado: false, 
      erro: 'INVALID_TOKEN_FORMAT',
      mensagem: 'Token não fornecido ou formato inválido. Use: Authorization: Bearer <token>'
    } 
  }];
}

const token = authHeader.slice(7).trim();
const parts = token.split('.');

// Validação 2: JWT deve ter 3 partes (header.payload.signature)
if (parts.length !== 3) {
  return [{ 
    json: { 
      autenticado: false, 
      erro: 'INVALID_JWT_FORMAT',
      mensagem: 'Token JWT mal formado'
    } 
  }];
}

const [headerB64, payloadB64, signatureB64] = parts;

// Validação 3: Verificar assinatura do JWT
if (!verifyJwtSignature(headerB64, payloadB64, signatureB64, JWT_SECRET)) {
  return [{ 
    json: { 
      autenticado: false, 
      erro: 'INVALID_SIGNATURE',
      mensagem: 'Assinatura do token inválida'
    } 
  }];
}

// Decode do payload
let payload;
try {
  payload = JSON.parse(base64UrlDecode(payloadB64));
} catch (e) {
  return [{ 
    json: { 
      autenticado: false, 
      erro: 'INVALID_JWT_PAYLOAD',
      mensagem: 'Não foi possível decodificar o payload do token'
    } 
  }];
}

// Validação 4: Token expirado
if (payload.exp && payload.exp * 1000 < Date.now()) {
  return [{ 
    json: { 
      autenticado: false, 
      erro: 'TOKEN_EXPIRED',
      mensagem: 'Token expirado',
      expirado_em: new Date(payload.exp * 1000).toISOString()
    } 
  }];
}

// Validação 5: Token deve ser do tipo 'access'
if (payload.type !== 'access') {
  return [{ 
    json: { 
      autenticado: false, 
      erro: 'INVALID_TOKEN_TYPE',
      mensagem: 'Token não é do tipo access',
      tipo_recebido: payload.type
    } 
  }];
}

// Validação 6: tenant_id obrigatório
if (!payload.tenant_id) {
  return [{ 
    json: { 
      autenticado: false, 
      erro: 'MISSING_TENANT_ID',
      mensagem: 'Token não contém tenant_id'
    } 
  }];
}

// Validação 7: user_id obrigatório
if (!payload.sub) {
  return [{ 
    json: { 
      autenticado: false, 
      erro: 'MISSING_USER_ID',
      mensagem: 'Token não contém ID do usuário (sub)'
    } 
  }];
}

// ✅ Token válido!
return [{
  json: {
    autenticado: true,
    usuario: {
      id: payload.sub,
      email: payload.email || null,
      tenant_id: payload.tenant_id,
      role: payload.role || 'user',
    },
    token_payload: payload,
    validado_em: new Date().toISOString()
  }
}];

