// ============================================
// GERAÇÃO DE TOKENS JWT (ACCESS + REFRESH)
// ============================================

const crypto = require('crypto');
const { v4: uuidv4 } = require('uuid');

const JWT_SECRET = process.env.JWT_SECRET || 'YOUR_SECRET_KEY_CHANGE_ME';

function base64UrlEncode(str) {
  return Buffer.from(str)
    .toString('base64')
    .replace(/\+/g, '-')
    .replace(/\//g, '_')
    .replace(/=/g, '');
}

function createJwt(payload, secret) {
  const header = { alg: 'HS256', typ: 'JWT' };

  const encodedHeader = base64UrlEncode(JSON.stringify(header));
  const encodedPayload = base64UrlEncode(JSON.stringify(payload));

  const signature = crypto
    .createHmac('sha256', secret)
    .update(`${encodedHeader}.${encodedPayload}`)
    .digest('base64url');

  return `${encodedHeader}.${encodedPayload}.${signature}`;
}

const usuario = $json.usuario;
const now = Math.floor(Date.now() / 1000);

// JTI únicos para cada token
const accessJti = uuidv4();
const refreshJti = uuidv4();

// Access Token (15 minutos)
const accessPayload = {
  sub: usuario.id,
  email: usuario.email,
  tenant_id: usuario.tenant_id,
  role: usuario.role,
  type: 'access',
  jti: accessJti,
  iat: now,
  exp: now + (15 * 60) // 15 minutos
};

// Refresh Token (7 dias)
const refreshPayload = {
  sub: usuario.id,
  tenant_id: usuario.tenant_id,
  type: 'refresh',
  jti: refreshJti,
  iat: now,
  exp: now + (7 * 24 * 60 * 60) // 7 dias
};

const accessToken = createJwt(accessPayload, JWT_SECRET);
const refreshToken = createJwt(refreshPayload, JWT_SECRET);

return [{
  json: {
    usuario,
    access_token: accessToken,
    refresh_token: refreshToken,
    access_jti: accessJti,
    refresh_jti: refreshJti,
    expires_in: 900, // 15 minutos em segundos
    token_type: 'Bearer',
    expires_at: new Date((now + 900) * 1000).toISOString(),
    refresh_expires_at: new Date(refreshPayload.exp * 1000).toISOString()
  }
}];

