// Processar parcelas e agrupar por tenant
const items = $input.all();
const tenantsMap = {};

for (const item of items) {
  const parcela = item.json;
  const tenantId = parcela.tenant_id;

  if (!tenantsMap[tenantId]) {
    tenantsMap[tenantId] = {
      tenant_id: tenantId,
      tenant_name: parcela.tenant_name,
      parcelas: [],
      total_valor: 0,
      total_parcelas: 0,
      parcelas_price: [],
      parcelas_juros: []
    };
  }

  // Calcular valor em aberto da parcela
  const valorAberto = parseFloat(parcela.valor_parcela || 0) - parseFloat(parcela.valor_pago || 0);

  // Dados para c치lculo Juros/Saldo
  const valorTotalEmprestimo = parseFloat(parcela.valor_total_emprestimo || 0);
  const valorJuros = parseFloat(parcela.interest_amount || 0);
  const valorPrincipal = parseFloat(parcela.principal_amount || 0);
  const totalPrincipalPago = parseFloat(parcela.total_principal_pago || 0);

  // Calcular saldo devedor real (Principal Total - Principal j치 pago)
  const saldoDevedor = valorTotalEmprestimo - totalPrincipalPago;

  // Determinar telefone do cliente e limpar caracteres
  let telefone = parcela.whatsapp || parcela.celular || parcela.telefone;
  let telefoneLimpo = null;

  if (telefone) {
    telefoneLimpo = telefone.replace(/\D/g, '');
    // Adicionar DDI 55 se n칚o tiver e parecer ser BR (10 ou 11 digitos)
    if (telefoneLimpo.length >= 10 && !telefoneLimpo.startsWith('55')) {
      telefoneLimpo = '55' + telefoneLimpo;
    }
  }

  const modelo = parcela.modelo_emprestimo || 'PRICE';

  const parcelaData = {
    parcela_id: parcela.parcela_id,
    client_id: parcela.client_id,
    cliente_nome: parcela.cliente_nome,
    cliente_phone: telefoneLimpo,
    cliente_phone_orig: telefone,
    cliente_email: parcela.cliente_email,
    numero_parcela: parcela.numero_parcela,
    valor_parcela: parseFloat(parcela.valor_parcela || 0),
    valor_aberto: valorAberto,
    valor_juros: valorJuros,
    valor_principal: valorPrincipal,
    saldo_devedor: saldoDevedor,
    status: parcela.status,
    data_vencimento: parcela.data_vencimento,
    modelo_emprestimo: modelo
  };

  tenantsMap[tenantId].parcelas.push(parcelaData);

  // Separar por modelo para facilitar formata칞칚o do Email
  if (modelo === 'PRICE') {
    tenantsMap[tenantId].parcelas_price.push(parcelaData);
  } else if (modelo === 'INTEREST_ONLY') {
    tenantsMap[tenantId].parcelas_juros.push(parcelaData);
  }

  tenantsMap[tenantId].total_valor += valorAberto;
  tenantsMap[tenantId].total_parcelas += 1;
}

// Converter mapa para array
const processedItems = Object.values(tenantsMap).map(tenant => ({
  json: tenant
}));

return processedItems;

const tenantData = $('Agrupar por Tenant').item.json;
const userEmail = $json.email;
const userName = $json.user_name || 'Parceiro';
const tenantName = tenantData.tenant_name;

if (!userEmail || !tenantName) return [];

const hoje = new Date().toLocaleDateString('pt-BR');

// Fun칞칚o Auxiliar de Moeda
function formatMoneyBR(v) {
  return Number(v ?? 0).toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 });
}

const parcelasPRICE = tenantData.parcelas_price || [];
const parcelasJUROS = tenantData.parcelas_juros || [];

// ---------------- START HTML ----------------
let html = `
