    .value { font-size: 14px; color: #2c3e50; font-weight: 600; }
    .highlight { color: #27ae60; }

    /* Bot칚o WhatsApp */
    .btn-whatsapp { 
      display: inline-block; 
      background-color: #25D366; 
      color: white; 
      text-decoration: none; 
      padding: 6px 12px; 
      border-radius: 15px; 
      font-size: 12px; 
      font-weight: bold; 
      text-align: center;
      margin-top: 5px;
    }
    .btn-whatsapp:hover { background-color: #1ebc57; }

    .footer { background-color: #ecf0f1; text-align: center; padding: 15px; font-size: 12px; color: #7f8c8d; }
  </style>
</head>
<body>
  <div class="container">
    <div class="header">
      <h1>游댒 Notifica칞칚o de Vencimentos</h1>
      <h2>${tenantName}</h2>
    </div>

    <div class="content">
      <div class="intro">
        Ol치 <strong>${userName}</strong>,<br>
        Seguem os clientes com vencimento hoje (${hoje}). Por favor, entre em contato para regulariza칞칚o.
      </div>
`;

// --- SE칂츾O 1: MODELO PRICE ---
if (parcelasPRICE.length > 0) {
  html += `<div class="section-title">游눱 Modelo PRICE (Parcelado)</div>`;

  parcelasPRICE.forEach(p => {
    const linkWpp = p.cliente_phone ? `https://wa.me/${p.cliente_phone}?text=Ol치 ${p.cliente_nome}, lembramos do vencimento da sua parcela hoje.` : '#';

    html += `
    <div class="client-card">
      <div class="card-header">
        <div class="client-name">${p.cliente_nome}</div>
        ${p.status === 'LATE' ? '<span class="client-status">ATRASADO</span>' : ''}
      </div>
      <div class="card-body">
        <div class="data-row">
          <span class="label">Contato</span>
          ${p.cliente_phone ? 
            `<a href="${linkWpp}" class="btn-whatsapp" target="_blank">游님 Chamar no WhatsApp</a>` : 
            '<span class="value">Sem telefone</span>'}
        </div>
        <div class="data-row">
          <span class="label">Valor Parcela</span>
          <span class="value highlight">R$ ${formatMoneyBR(p.valor_parcela)}</span>
        </div>
      </div>
    </div>`;
  });
}

// --- SE칂츾O 2: MODELO SOMENTE JUROS ---
if (parcelasJUROS.length > 0) {
  html += `<div class="section-title">游늳 Modelo Somente Juros</div>`;

  parcelasJUROS.forEach(p => {
    const linkWpp = p.cliente_phone ? `https://wa.me/${p.cliente_phone}?text=Ol치 ${p.cliente_nome}, lembramos do vencimento dos juros hoje.` : '#';

    html += `
    <div class="client-card">
      <div class="card-header">
        <div class="client-name">${p.cliente_nome}</div>
      </div>
      <div class="card-body" style="grid-template-columns: 1fr 1fr;">
        <div class="data-row">
          <span class="label">Contato</span>
          ${p.cliente_phone ? 
            `<a href="${linkWpp}" class="btn-whatsapp" target="_blank">游님 Chamar no WhatsApp</a>` : 
            '<span class="value">Sem telefone</span>'}
        </div>
        <div class="data-row">
          <span class="label">Valor Juros</span>
          <span class="value highlight">R$ ${formatMoneyBR(p.valor_juros)}</span>
        </div>
        <div class="data-row" style="grid-column: span 2; margin-top: 8px; border-top: 1px solid #eee; padding-top: 8px;">
          <span class="label">游눯 Valor Restante em Aberto (Principal)</span>
          <span class="value">R$ ${formatMoneyBR(p.saldo_devedor)}</span>
        </div>
      </div>
    </div>`;
  });
}

// --- FOOTER ---
html += `
      <div style="margin-top: 30px; text-align: center; color: #999; font-size: 13px;">
        <p>Total de Vencimentos: <strong>${tenantData.total_parcelas}</strong> | Total a Receber: <strong>R$ ${formatMoneyBR(tenantData.total_valor)}</strong></p>
      </div>
    </div>
    <div class="footer">
      Enviado automaticamente pelo Agente CredGestor
    </div>
  </div>
</body>
</html>
`;

return [{
  json: {
    email: userEmail,
    user_name: userName,
    assunto: `游댒 Vencimentos do Dia - ${tenantName} - ${hoje}`,
    html_message: html
  }
}];

// Formatar mensagens WhatsApp individuais para clientes
const tenantData = $('Agrupar por Tenant').item.json;
const processedItems = [];

const hoje = new Date().toLocaleDateString('pt-BR');

function formatMoneyBR(v) {
  return Number(v ?? 0).toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 });
}

function formatPhone(phone) {
  if (!phone) return '';
  const cleaned = phone.replace(/^55/, '');
  if (cleaned.length === 11) {
    return `(${cleaned.substring(0, 2)}) ${cleaned.substring(2, 7)}-${cleaned.substring(7)}`;
  }
  return cleaned;
}

for (const parcela of tenantData.parcelas) {
  // S칩 processar se tiver telefone v치lido
  if (!parcela.cliente_phone) {
    continue;
  }

  let mensagem = '';

  // Mensagem para modelo PRICE
  if (parcela.modelo_emprestimo === 'PRICE') {
    mensagem = `游댒 *Lembrete de Vencimento*\n\n`;
    mensagem += `Ol치 *${parcela.cliente_nome}*!\n\n`;
    mensagem += `Sua parcela #${parcela.numero_parcela} vence hoje (${hoje}).\n\n`;
    mensagem += `游눯 *Valor da Parcela:* R$ ${formatMoneyBR(parcela.valor_parcela)}\n\n`;
    mensagem += `Por favor, realize o pagamento para evitar juros e multa.\n\n`;
    mensagem += `Obrigado!\n`;
    mensagem += `_Sistema CredGestor_`;
  }
  // Mensagem para modelo INTEREST_ONLY (Somente Juros)
  else if (parcela.modelo_emprestimo === 'INTEREST_ONLY') {
    mensagem = `游댒 *Lembrete de Vencimento - Juros*\n\n`;
    mensagem += `Ol치 *${parcela.cliente_nome}*!\n\n`;
    mensagem += `O pagamento dos juros do seu empr칠stimo vence hoje (${hoje}).\n\n`;
    mensagem += `游눯 *Valor dos Juros:* R$ ${formatMoneyBR(parcela.valor_juros)}\n`;
    mensagem += `游늵 *Saldo Devedor Restante:* R$ ${formatMoneyBR(parcela.saldo_devedor)}\n\n`;
    mensagem += `Por favor, realize o pagamento.\n\n`;
    mensagem += `Obrigado!\n`;
    mensagem += `_Sistema CredGestor_`;
  }

  if (mensagem) {
    processedItems.push({
      json: {
        telefone: parcela.cliente_phone,
        mensagem: mensagem,
        cliente_nome: parcela.cliente_nome,
        parcela_id: parcela.parcela_id,
        numero_parcela: parcela.numero_parcela,
        modelo_emprestimo: parcela.modelo_emprestimo,
        valor: parcela.modelo_emprestimo === 'INTEREST_ONLY' ? parcela.valor_juros : parcela.valor_parcela
      }
    });
  }
}

return processedItems;
